import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';

const Content = () => {
	return (
		<Container>
			<Grid container direction='row-reverse' justify='center' alignItems='center' style={{ height: '77vh' }}>
				<Grid item style={{ direction: 'rtl', marginLeft: 40 }}>
					<h1 style={{ fontSize: '2.5em', fontWeight: 100, marginBottom: '15px' }}>تایپ آنلاین، به‌صرفه!</h1>
					<p style={{ fontSize: '1.5em', textAlign: 'justify', maxWidth: '550px' }}>
						تیزتایپ همواره در تلاش است تا بهترین تجربه‌ی سفارش، انجام و تحویل پروژه‌های تایپ بصورت آنلاین را برای شما به
						ارمغان بیاورد.
					</p>
				</Grid>
				<Grid item>
					<Paper elevation={0} style={{ padding: 30, width: 400, height: 500 }}>
						<FormControl style={{ width: 340 }}>
							<label htmlFor='phone-number' style={{ direction: 'rtl' }}>
								شماره تلفن همراه <span style={{ color: 'red' }}>*</span>
							</label>
							<TextField id='phone-number' variant='outlined' required size='small' style={{ marginTop: 10 }} />
							<label htmlFor='password' style={{ direction: 'rtl', marginTop: 10 }}>
								کلمه عبور<span style={{ color: 'red' }}>*</span>
							</label>
							<TextField id='password' variant='outlined' required size='small' style={{ marginTop: 10 }} />
							<RadioGroup row aria-label='position' name='position' defaultValue='top' style={{ marginTop: 10 }}>
								<Grid container justify='space-between'>
									<Grid item>
										<FormControlLabel
											value='end'
											control={<Radio color='primary' />}
											label='انجام پروژه (تایپیست)'
											style={{ fontSize: '10px' }}
										/>
									</Grid>
									<Grid item>
										<FormControlLabel
											value='start'
											control={<Radio color='primary' />}
											label='ثبت پروژه (کارفرما)'
											labelPlacement='start'
										/>
									</Grid>
								</Grid>
							</RadioGroup>
							<Button variant='contained' color='primary' style={{ marginTop: 10 }}>
								عضویت
							</Button>
						</FormControl>
					</Paper>
				</Grid>
			</Grid>
		</Container>
	);
};

export default Content;
