import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';

const Content = () => {
	return (
		<Container>
			<Grid container direction='row-reverse' justify='center' alignItems='center' style={{ height: '80vh' }}>
				<Grid item style={{ direction: 'rtl', marginLeft: 40 }}>
					<h1 style={{ fontSize: '2.5em', fontWeight: 100, marginBottom: '15px' }}>تایپ آنلاین، به صرفه!</h1>
					<p style={{ fontSize: '1.5em', textAlign: 'justify', maxWidth: '550px' }}>
						تیزتایپ همواره در تلاش است تا بهترین تجربه‌ی سفارش و انجام پروژه‌های تایپ بصورت آنلاین را برای شما به ارمغان
						بیاورد.
					</p>
				</Grid>
				<Grid item>
					<Paper elevation={0} style={{ padding: 30, width: 400, height: 500, opacity: 0.5 }}>
						<FormControl>
							<TextField label='شماره تلفن' variant='outlined' autoFocus={true} required={true} />
							<TextField label='رمز' variant='outlined' style={{ marginTop: 10 }} required={true} />
						</FormControl>
					</Paper>
				</Grid>
			</Grid>
		</Container>
	);
};

export default Content;
