import React from 'react';
import Grid from '@material-ui/core/Grid';

const Introduction = () => {
	return (
		<div>
			<Grid
				item
				container
				direction='row-reverse'
				justify='space-around'
				alignItems='center'
				className='intro-wrapper border'
			>
				<Grid item xs={6} class='intro'>
					<h2 className='intro-heading'>کارفرما</h2>
					<h3 className='intro-item'>پروژه تابپت رو ثبت کن.</h3>
					<h3 className='intro-item'>از بین پیشنهاد ها، تایپیست مورد نظر رو انتخاب کن.</h3>
					<h3 className='intro-item'>هزینه‌ش رو پرداخت کن و نسخه ی تایپ شده‌ش رو تحویل بگیر.</h3>
				</Grid>
				<Grid item xs={6} class='intro'>
					<h2 className='intro-heading'>تایپیست</h2>
					<h3 className='intro-item'>پروژه تابپت رو ثبت کن</h3>
					<h3 className='intro-item'>از بین پیشنهاد ها، تایپیست مورد نظر رو انتخاب کن</h3>
					<h3 className='intro-item'>هزینه‌ش رو پرداخت کن و نسخه ی تایپ شده‌ش رو تحویل بگیر</h3>
				</Grid>
			</Grid>
		</div>
	);
};

export default Introduction;
