import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';

const BootstrapInput = withStyles(theme => ({
	root: {
		'label + &': {
			marginTop: theme.spacing(3),
		},
	},
	input: {
		borderRadius: 4,
		position: 'relative',
		backgroundColor: theme.palette.common.white,
		border: '1px solid #ced4da',
		fontSize: 16,
		width: 'auto',
		padding: '10px 12px',
		transition: theme.transitions.create(['border-color', 'box-shadow']),
		// Use the system font instead of the default Roboto font.
		fontFamily: [
			'-apple-system',
			'BlinkMacSystemFont',
			'"Segoe UI"',
			'Roboto',
			'"Helvetica Neue"',
			'Arial',
			'sans-serif',
			'"Apple Color Emoji"',
			'"Segoe UI Emoji"',
			'"Segoe UI Symbol"',
		].join(','),
		'&:focus': {
			boxShadow: `${fade(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
			borderColor: theme.palette.primary.main,
		},
	},
}))(InputBase);

const Content = () => {
	return (
		<Container>
			<Grid container direction='row-reverse' justify='center' alignItems='center' style={{ height: '77vh' }}>
				<Grid item style={{ direction: 'rtl', marginLeft: 40 }}>
					<h1 style={{ fontSize: '2.5em', fontWeight: 100, marginBottom: '15px' }}>تایپ آنلاین، به‌صرفه!</h1>
					<p style={{ fontSize: '1.5em', textAlign: 'justify', maxWidth: '550px' }}>
						تیزتایپ همواره در تلاش است تا بهترین تجربه‌ی سفارش و انجام پروژه‌های تایپ بصورت آنلاین را برای شما به ارمغان
						بیاورد.
					</p>
				</Grid>
				<Grid item>
					<Paper elevation={0} style={{ padding: 30, width: 400, height: 500 }}>
						<FormControl style={{ width: 340 }}>
							<InputLabel shrink htmlFor='bootstrap-input'>
								Bootstrap
							</InputLabel>
							<BootstrapInput defaultValue='react-bootstrap' id='bootstrap-input' />
							<TextField label='رمز' variant='outlined' style={{ marginTop: 10 }} required={true} size='small' />
							<Button variant='contained' color='primary' style={{ marginTop: 10 }}>
								عضویت
							</Button>
						</FormControl>
					</Paper>
				</Grid>
			</Grid>
		</Container>
	);
};

export default Content;
