import React from 'react';
import Grid from '@material-ui/core/Grid';
import { useSpring, animated } from 'react-spring';

const Heading = () => {
	const heading = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }, { marginLeft: 150 }],
		config: { tension: 400, friction: 40 },
	});
	const headingAdder = useSpring({
		from: { opacity: 0, marginTop: 200 },
		to: async next => {
			await next({ opacity: 1, marginTop: 80 });
			await next({ opacity: 0, marginTop: -5 });
		},
		delay: 800,
	});
	return (
		<div>
			<Grid container direction='column' alignItems='center'>
				<animated.h1 className='heading' style={heading}>
					تایپ آنلاین
				</animated.h1>
				<animated.h1 className='heading-adder' style={headingAdder}>
					به‌صرفه
				</animated.h1>
			</Grid>
		</div>
	);
};

export default Heading;
