import React, { useState, useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import LocalCafeOutlinedIcon from '@material-ui/icons/LocalCafeOutlined';
import KeyboardIcon from '@material-ui/icons/Keyboard';
import { Transition } from 'react-spring/renderprops';
import { useSpring, animated } from 'react-spring';

const defaultStyles = {
	overflow: 'hidden',
	color: 'white',
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	fontSize: '1.5em',
	fontFamily: 'TizTypeFont',
	direction: 'rtl',
	marginTop: '10px',
};

const Introduction = () => {
	const [items, setItems] = useState([]);
	const [items2, setItems2] = useState([]);

	useEffect(() => {
		setItems([
			'پروژه تایپت رو ثبت کن.',
			'از بین پیشنهاد ها، تایپیست مورد نظرت رو انتخاب کن.',
			'هزینه‌ش رو پرداخت کن و نسخه ی تایپ شده‌ش رو تحویل بگیر.',
		]);
		setItems2([
			'پیشنهادت برای انجام پروژه رو ثبت کن.',
			'بعد از تایید شدن پیشنهادت، پروژه رو انجام بده.',
			'پروژه‌ی انجام شده رو برای کارفرما ارسال کن و هزینه‌ش رو دریافت کن.',
		]);
	}, []);

	const heading = useSpring({
		from: { opacity: 0, marginTop: 120 },
		to: { opacity: 1, marginTop: 0 },
		config: { tension: 280, friction: 40 },
	});

	const button = useSpring({
		from: { opacity: 0, marginLeft: 200 },
		to: { opacity: 1, marginLeft: 0 },
		config: { tension: 280, friction: 40 },
	});

	return (
		<Grid item container direction='row-reverse' justify='space-around' alignItems='center' className='intro-wrapper'>
			<Grid item container xs={6} direction='column' className='intro'>
				<Grid item>
					<animated.h2 className='intro-heading' style={heading}>
						کارفرما
					</animated.h2>
				</Grid>
				<Grid item>
					<Transition
						items={items}
						from={{ overflow: 'hidden', height: 0, opacity: 0 }}
						enter={{ height: 50, opacity: 1 }}
						trail={200}
					>
						{item => styles => <animated.div style={{ ...defaultStyles, ...styles }}>{item}</animated.div>}
					</Transition>
				</Grid>
				<Grid item>
					<animated.Button
						style={button}
						variant='outlined'
						size='large'
						color='primary'
						startIcon={<LocalCafeOutlinedIcon />}
					>
						سفارش تایپ
					</animated.Button>
				</Grid>
			</Grid>
			<Grid item container xs={6} direction='column' className='intro'>
				<Grid item>
					<animated.h2 className='intro-heading' style={heading}>
						تایپیست
					</animated.h2>
				</Grid>
				<Grid item>
					<Transition
						items={items2}
						from={{ overflow: 'hidden', height: 0, opacity: 0 }}
						enter={{ height: 50, opacity: 1 }}
						trail={200}
					>
						{item => styles => <animated.div style={{ ...defaultStyles, ...styles }}>{item}</animated.div>}
					</Transition>
				</Grid>
				<Grid item>
					<Button variant='outlined' size='large' color='primary' startIcon={<KeyboardIcon />}>
						انجام پروژه
					</Button>
				</Grid>
			</Grid>
		</Grid>
	);
};

export default Introduction;
