import React from 'react';
import { useSpring, animated, config } from 'react-spring';

const Introduction = () => {
	const props = useSpring({
		from: { opacity: 0, marginTop: 150, marginLeft: 0 },
		to: async next => {
			await next({ opacity: 1, marginTop: 0 });
			await next({ marginLeft: 500 });
		},
		config: { duration: 1000 },
		delay: 500,
	});
	return (
		<animated.div style={props} className='rtl'>
			<h1 className='heading'>تایپ آنلاین</h1>
		</animated.div>
	);
};

export default Introduction;
