import React from 'react';
import { Transition, animated } from 'react-spring/renderprops';

const defaultStyles = {
	overflow: 'hidden',
	width: '100%',
	color: 'white',
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	fontSize: '2em',
	fontFamily: "'Kanit', sans-serif",
	textTransform: 'uppercase',
};

export default class TransitionsExample extends React.PureComponent {
	state = { items: [] };

	componentDidMount() {
		this.setState({ items: ['Apples', 'Oranges', 'Bananas'] });
	}

	render() {
		return (
			<div
				style={{
					overflow: 'hidden',
					margin: 0,
					padding: 0,
				}}
			>
				<Transition
					items={this.state.items}
					from={{ overflow: 'hidden', height: 0, opacity: 0 }}
					enter={{ height: 50, opacity: 1 }}
					trail={200}
				>
					{item => styles => <animated.div style={{ ...styles }}>{item}</animated.div>}
				</Transition>
			</div>
		);
	}
}
