import ReactDOM from 'react-dom';
import React from 'react';
import { useSpring, animated } from 'react-spring';
import range from 'lodash-es/range';
import './index.css';

const items = range(10);
const interp = i => r => `translate3d(0, ${27 * Math.sin(r + (i * 2 * Math.PI) / 1.6)}px, 0)`;

export default function App() {
	const { radians } = useSpring({
		to: async next => {
			while (5) await next({ radians: 6 * Math.PI });
		},
		from: { radians: 0 },
		config: { duration: 2000 },
		reset: true,
	});
	return items.map(i => (
		<animated.div key={i} className='script-bf-box' style={{ transform: radians.interpolate(interp(i)) }} />
	));
}

ReactDOM.render(<App />, document.getElementById('root'));
