import React from 'react';
import Employer from '../../../utils/img/employer.svg';

import Grid from '@material-ui/core/Grid';

const Introduction = props => {
	return (
		<div className='rtl'>
			<h1 className='heading'>!تایپ آنلاین به‌صرفه</h1>
			<Grid container direction='column' justify='center' align-items='center'>
				<Grid item container xs={12} className='employer' justify='space-between'>
					<Grid item xs={5}>
						<h3 className='employer-item'>پروژه‌ی تایپ‌تون رو ثبت کنید.</h3>
						<h3 className='employer-item'>تایپیست مورد نظرتون رو انتخاب کنید.</h3>
						<h3 className='employer-item'>پروژه‌تون رو تحویل بگیرین.</h3>
					</Grid>
					<Grid item xs={3}>
						<img src={Employer} alt='employer' className='employer-svg' />
					</Grid>
				</Grid>
				<Grid item container xs={12} clsasName='border'>
					<Grid item xs={5} className='rtl'>
						<h3 className='employee-item'>برای انجام پروژه درخواست بدین.</h3>
						<h3 className='employee-item'>پروژه رو انجام بدین.</h3>
						<h3 className='employee-item'>هزینه‌ش رو دریافت کنین.</h3>
					</Grid>
				</Grid>
			</Grid>
		</div>
	);
};

export default Introduction;
