import React from 'react';
import Grid from '@material-ui/core/Grid';
import { useSpring, animated } from 'react-spring';

const Header = () => {
	const header = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }],
		config: { tension: 400, friction: 40 },
	});
	const headerAdder = useSpring({
		from: { opacity: 0, marginTop: 200 },
		to: { opacity: 1, marginTop: 80 },
		delay: 800,
		config: { tension: 400, friction: 40 },
	});
	return (
		<div>
			<Grid container direction='row-reverse' justify='center' alignItems='center' className='border'>
				<animated.Grid item className='border' style={header}>
					<h1 className='heading'>تایپ آنلاین</h1>
				</animated.Grid>
				<Grid item className='border'>
					<animated.h1 className='heading' style={headerAdder}>
						به‌صرفه
					</animated.h1>
				</Grid>
			</Grid>
		</div>
	);
};

export default Header;
