import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';

const Content = () => {
	return (
		<Container>
			<Grid container direction='row' justify='center' style={{ padding: '20px 0', direction: 'rtl' }}>
				<Grid item xs={4}>
					<div style={{ maxWidth: '310px', textAlign: 'justify' }}>
						<h3>به تیزتایپ خوش آمدید</h3>
						<p>تیزتایپ بستری مناسب و کارآمد برای سفارش و انجام پروژه‌های تایپ بصورت آنلاین می‌باشد.</p>
						<p>مقدم شمردن حقوق و مزایای کارفرماها و تایپیست‌ها اولین اولویت تیزتایپ است.</p>
						<Button variant='contained' color='primary' startIcon={<ChevronLeftIcon />} style={{ direction: 'ltr' }}>
							همین حالا عضو شوید
						</Button>
					</div>
				</Grid>
				<Grid item xs={4}></Grid>
				<Grid item xs={4}></Grid>
			</Grid>
		</Container>
	);
};

export default Content;
