import React, { useEffect, useState } from 'react';
import Box from '@material-ui/core/Box';
import Fade from '@material-ui/core/Fade';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/styles';

const useStyles = makeStyles({
	container: {
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'center',
		alignItems: 'center',
	},
	heading: {
		padding: '2.5em 0 1em',
		fontSize: '2em',
	},
	subHeading: {
		fontSize: '1.3em',
		color: 'rgb(0,0,0,0.5)',
	},
});

const Heading = () => {
	let [show, setShow] = useState(false);
	const classes = useStyles();

	useEffect(() => {
		console.log(show);
	}, [show]);

	const onScroll = () => {};

	return (
		<Box id='why-tiz-type' className={classes.container}>
			<Fade in={window.scrollY >= 400 ? true : false} timeout={1000}>
				<Typography componant='h2' variant='body2' className={classes.heading}>
					چرا تیزتایپ؟
				</Typography>
			</Fade>
			<Typography variant='body2' className={classes.subHeading}>
				سریع و امن، راحت و کاربرپسند، کارا و به‌صرفه
			</Typography>
		</Box>
	);
};

export default Heading;
