import React from 'react';
import Grid from '@material-ui/core/Grid';
import { useSpring, animated } from 'react-spring';

const Header = () => {
	const header = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }, { marginLeft: 150 }],
		config: { tension: 400, friction: 40 },
	});
	const headerAdder = useSpring({
		from: { opacity: 0, marginTop: 200 },
		to: { opacity: 1, marginTop: 80 },
		delay: 800,
		config: { tension: 400, friction: 40 },
	});
	return (
		<div>
			<animated.h1 className='heading' style={header}>
				تایپ آنلاین
			</animated.h1>
			<animated.h1 className='heading' style={headerAdder}>
				به‌صرفه
			</animated.h1>
		</div>
	);
};

export default Header;
