import React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { useSpring, animated, config } from 'react-spring';

const Header = () => {
	const header = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }, { marginLeft: 150 }],
		immediate: true,
		config: {},
	});
	const headerAdder = useSpring({
		from: { opacity: 0, marginTop: 200 },
		to: { opacity: 1, marginTop: 80 },
		delay: 800,
	});
	return (
		<div>
			<Grid container direction='column' alignItems='center'>
				<animated.h1 className='heading' style={header}>
					تایپ آنلاین
				</animated.h1>
				<animated.h1 className='heading-adder' style={headerAdder}>
					به‌صرفه
				</animated.h1>
			</Grid>
		</div>
	);
};

export default Header;
