import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';
import { create } from 'jss';
import rtl from 'jss-rtl';
import { StylesProvider, jssPreset } from '@material-ui/core/styles';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';

const theme = createMuiTheme({
	direction: 'rtl', // Both here and <body dir="rtl">
});

const jss = create({ plugins: [...jssPreset().plugins, rtl()] });

const Content = props => {
	return (
		<Container>
			<StylesProvider jss={jss}>{props.children}</StylesProvider>
			<Grid container direction='row-reverse' justify='center' alignItems='center' style={{ height: '77vh' }}>
				<Grid item style={{ direction: 'rtl', marginLeft: 40 }}>
					<h1 style={{ fontSize: '2.5em', fontWeight: 100, marginBottom: '15px' }}>تایپ آنلاین، به‌صرفه!</h1>
					<p style={{ fontSize: '1.5em', textAlign: 'justify', maxWidth: '550px' }}>
						تیزتایپ همواره در تلاش است تا بهترین تجربه‌ی سفارش، انجام و تحویل پروژه‌های تایپ بصورت آنلاین را برای شما به
						ارمغان بیاورد.
					</p>
				</Grid>
				<Grid item>
					<Paper elevation={0} style={{ padding: 30, width: 400, height: 500 }}>
						<FormControl style={{ width: 340 }}>
							<ThemeProvider theme={theme}>
								<div dir='rtl'>
									<TextField placeholder='Name' />
									<input type='text' placeholder='Name' />
								</div>
							</ThemeProvider>
							<TextField label='رمز' variant='outlined' style={{ marginTop: 10 }} required={true} size='small' />
							<Button variant='contained' color='primary' style={{ marginTop: 10 }}>
								عضویت
							</Button>
						</FormControl>
					</Paper>
				</Grid>
			</Grid>
		</Container>
	);
};

export default Content;
