import React, { useState, useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import LocalCafeOutlinedIcon from '@material-ui/icons/LocalCafeOutlined';
import KeyboardIcon from '@material-ui/icons/Keyboard';
import { useSpring, Transition, animated } from 'react-spring/renderprops';

const defaultStyles = {
	overflow: 'hidden',
	color: 'white',
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	fontSize: '1.5em',
	fontFamily: 'TizTypeFont',
	direction: 'rtl',
	marginTop: '10px',
};

const Introduction = () => {
	const heading = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }, { marginLeft: 180 }],
		config: { tension: 280, friction: 40 },
	});

	return (
		<Grid item container direction='row-reverse' justify='space-around' alignItems='center' className='intro-wrapper'>
			<Grid item container xs={6} direction='column' className='intro border'>
				<Grid item className='border'>
					<animated.h2 className='intro-heading' style={heading}>
						کارفرما
					</animated.h2>
				</Grid>
				<Grid item className='border'>
					<Transition
						items={items}
						from={{ overflow: 'hidden', height: 0, opacity: 0 }}
						enter={{ height: 50, opacity: 1 }}
						trail={200}
					>
						{item => styles => <animated.div style={{ ...defaultStyles, ...styles }}>{item}</animated.div>}
					</Transition>
				</Grid>
				<Grid item className='border'>
					<Button variant='outlined' size='large' color='primary' startIcon={<LocalCafeOutlinedIcon />}>
						سفارش تایپ
					</Button>
				</Grid>
			</Grid>
			<Grid item xs={6} className='intro'>
				<h2 className='intro-heading'>تایپیست</h2>
				<h3 className='intro-item'>پیشنهادت برای انجام پروژه رو ثبت کن.</h3>
				<h3 className='intro-item'>بعد از تایید شدن پیشنهادت، پروژه رو انجام بده.</h3>
				<h3 className='intro-item'>پروژه‌ی انجام شده رو برای کارفرما ارسال کن و هزینه‌ش رو دریافت کن.</h3>
				<Button variant='outlined' size='large' color='primary' startIcon={<KeyboardIcon />}>
					انجام پروژه
				</Button>
			</Grid>
		</Grid>
	);
};

export default Introduction;
