import React from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import LocalCafeOutlinedIcon from '@material-ui/icons/LocalCafeOutlined';

const useStyles = makeStyles(theme => ({
	button: {
		margin: theme.spacing(1),
	},
}));

const Introduction = () => {
	const classes = useStyles();
	return (
		<div>
			<Grid
				item
				container
				direction='row-reverse'
				justify='space-around'
				alignItems='center'
				className={(classes.button, 'intro-wrapper')}
			>
				<Grid item xs={6} class='intro'>
					<h2 className='intro-heading'>کارفرما</h2>
					<h3 className='intro-item'>پروژه تایپت رو ثبت کن.</h3>
					<h3 className='intro-item'>از بین پیشنهاد ها، تایپیست مورد نظرت رو انتخاب کن.</h3>
					<h3 className='intro-item'>هزینه‌ش رو پرداخت کن و نسخه ی تایپ شده‌ش رو تحویل بگیر.</h3>
					<Button variant='outlined' size='large' color='primary' endIcon={<LocalCafeOutlinedIcon />}>
						سفارش تایپ
					</Button>
				</Grid>
				<Grid item xs={6} class='intro'>
					<h2 className='intro-heading'>تایپیست</h2>
					<h3 className='intro-item'>پیشنهادت برای انجام پروژه رو ثبت کن.</h3>
					<h3 className='intro-item'>بعد از تایید شدن پیشنهادت، پروژه رو انجام بده.</h3>
					<h3 className='intro-item'>پروژه‌ی انجام شده رو برای کارفرما ارسال کن و هزینه‌ش رو دریافت کن.</h3>
				</Grid>
			</Grid>
		</div>
	);
};

export default Introduction;
