import React from 'react';
import Grid from '@material-ui/core/Grid';
import { useSpring, animated } from 'react-spring';

const Heading = () => {
	const heading = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }, { marginLeft: 180 }],
		config: { tension: 400, friction: 40 },
	});
	const headingAdder1 = useSpring({
		from: { opacity: 0, marginTop: 200 },
		to: { opacity: 1, marginTop: 80 },
		config: { tension: 400, friction: 40 },
		delay: 850,
	});
	return (
		<div>
			<Grid container direction='row-reverse' justify='center' alignItems='center'>
				<Transition
					items={items}
					keys={item => item.key}
					from={{ transform: 'translate3d(0,-40px,0)' }}
					enter={{ transform: 'translate3d(0,0px,0)' }}
					leave={{ transform: 'translate3d(0,-40px,0)' }}
				>
					{item => props => <div style={props}>{item.text}</div>}
				</Transition>
			</Grid>
		</div>
	);
};

export default Heading;
