import React from 'react';
import { Transition, animated } from 'react-spring/renderprops';

const defaultStyles = {
	overflow: 'hidden',
	width: '100%',
	color: 'white',
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	fontSize: '2em',
	fontFamily: 'TizTypeFont',
};

export default class TransitionsExample extends React.PureComponent {
	state = { items: [] };

	componentDidMount() {
		this.setState({
			items: [
				'پروژه تایپت رو ثبت کن.',
				'از بین پیشنهاد ها، تایپیست مورد نظرت رو انتخاب کن.',
				'هزینه‌ش رو پرداخت کن و نسخه ی تایپ شده‌ش رو تحویل بگیر.',
			],
		});
	}

	render() {
		return (
			<div
				style={{
					overflow: 'hidden',
					margin: 0,
					padding: 0,
				}}
			>
				<Transition
					items={this.state.items}
					from={{ overflow: 'hidden', height: 0, opacity: 0 }}
					enter={{ height: 50, opacity: 1 }}
					trail={200}
				>
					{item => styles => <animated.div style={{ ...defaultStyles, ...styles }}>{item}</animated.div>}
				</Transition>
			</div>
		);
	}
}
