import React from 'react';

import { animated, useSpring } from 'react-spring';

const Introduction = props => {
	const fade = useSpring({ from: { marginLeft: 100 }, marginLeft: 0 });

	return (
		<animated.div className='rtl' style={fade}>
			<h1 className='heading'>!تایپ آنلاین به‌صرفه</h1>
		</animated.div>
	);
};

export default Introduction;
