import React from 'react';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Divider from '@material-ui/core/Divider';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
	wrapper: {
		maxWidth: '500px',
	},
	heading: {
		fontSize: '2.5em',
		fontWeight: 100,
		marginBottom: '15px',
		color: '#FFF',
	},
	paragraph: {
		fontSize: '1.5em',
		textAlign: 'left',
		maxWidth: '550px',
		color: 'rgb(255, 255, 255, 0.8)',
	},
});

const Content = () => {
	const classes = useStyles();

	return (
		<Box className={classes.wrapper}>
			<Typography display='inline' component='h1' className={classes.heading}>
				تایپ آنلاین، به‌صرفه!
			</Typography>
			<Divider component='br' />
			<Typography display='inline' className={classes.paragraph}>
				تیزتایپ همواره در تلاش است تا بهترین تجربه‌ی سفارش، انجام و تحویل پروژه‌های تایپ بصورت آنلاین را برای شما به
				ارمغان بیاورد.
			</Typography>
		</Box>
	);
};

export default Content;
