import React from 'react';
import { Transition, animated } from 'react-spring/renderprops';

const defaultStyles = {
	overflow: 'hidden',
	width: '100%',
	color: 'white',
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	fontSize: '3em',
	fontFamily: 'TizTypeFont',
};

class Heading extends React.PureComponent {
	state = { items: [] };

	async componentDidMount() {
		this.t1 && clearTimeout(this.t1);
		this.t2 && clearTimeout(this.t2);
		this.t3 && clearTimeout(this.t3);

		this.setState({ items: ['نارنگی', 'Oranges', 'Bananas'] });
		this.t1 = setTimeout(() => this.setState({ items: ['Apples', 'Bananas'] }), 1500);
		this.t2 = setTimeout(() => this.setState({ items: ['Apples', 'Oranges', 'Bananas'] }), 3000);
		this.t3 = setTimeout(() => this.setState({ items: ['Kiwis'] }), 4500);
	}

	render() {
		return (
			<div>
				<Transition
					items={this.state.items}
					//initial={null}
					from={{ overflow: 'hidden', height: 0, opacity: 0 }}
					enter={{ height: 700, opacity: 1 }}
					leave={{ height: 0, opacity: 0 }}
					trail={200}
				>
					{item => styles => <animated.div style={{ ...defaultStyles, ...styles }}>{item}</animated.div>}
				</Transition>
			</div>
		);
	}
}

export default Heading;
