import React from 'react';
import { Transition, animated } from 'react-spring/renderprops';

const defaultStyles = {
	overflow: 'hidden',
	width: '100%',
	color: 'white',
	display: 'flex',
	justifyContent: 'center',
	alignItems: 'center',
	fontSize: '3em',
	fontFamily: 'TizTypeFont',
};

class Heading extends React.PureComponent {
	state = { items: [] };

	async componentDidMount() {
		this.setState({ items: ['به‌صرفه'] });
	}

	render() {
		return (
			<div style={{ marginTop: '80px' }}>
				<Transition
					items={this.state.items}
					//initial={null}
					from={{ overflow: 'hidden', height: 0, opacity: 0 }}
					enter={{ height: 70, opacity: 1 }}
					leave={{ height: 0, opacity: 0 }}
					trail={200}
				>
					{item => styles => <animated.div style={{ ...defaultStyles, ...styles }}>{item}</animated.div>}
				</Transition>
			</div>
		);
	}
}

export default Heading;
