import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';

const Content = () => {
	return (
		<Container>
			<Grid
				container
				direction='row-reverse'
				justify='space-between'
				alignItems='center'
				className='border'
				style={{ height: '80vh' }}
			>
				<Grid item style={{ direction: 'rtl' }}>
					<h1 style={{ fontSize: '2.5em', fontWeight: 100, marginBottom: '10px' }}>تایپ آنلاین، به صرفه!</h1>
					<p style={{ fontSize: '1.5em', textAlign: 'justify', maxWidth: '550px' }}>
						تیزتایپ همواره در تلاش است تا بهترین تجربه‌ی سفارش و انجام پروژه‌های تایپ بصورت آنلاین را، هم برای تایپیست و
						هم برای کارفرما، به ارمغان بیاورد.
					</p>
				</Grid>
			</Grid>
		</Container>
	);
};

export default Content;
