import React, { Component } from 'react';
import ReactTextTransition, { presets } from 'react-text-transition';

const randomNumber = () => Math.floor(Math.random() * 9999999999 + 10000000000);

const texts = ['Fast', 'Scalable', 'Potato'];

class Demo extends Component {
	state = {
		number: randomNumber(),
		textIndex: 0,
	};

	componentDidMount() {
		setInterval(() => {
			this.setState({
				number: randomNumber(),
				textIndex: this.state.textIndex + 1,
				paragraphIndex: this.state.paragraphIndex + 1,
			});
		}, 2000);
	}

	render() {
		return (
			<Grid>
				تایپ آنلاین
				<ReactTextTransition text={texts[this.state.textIndex % texts.length]} style={{ margin: '0 4px' }} inline />
			</Grid>
		);
	}
}

export default Demo;
