import React from 'react';
import Grid from '@material-ui/core/Grid';
import { useSpring, animated, config } from 'react-spring';

const Heading = () => {
	const headingAdder = () =>
		useSpring({
			from: { opacity: 0, marginTop: 120 },
			to: async next => {
				while (1) {
					await next({ opacity: 0, marginTop: 120 });
					await next({ opacity: 1, marginTop: 80 });
					await next({ opacity: 0, marginTop: 40 });
				}
			},
			config: { tension: 280, friction: 100 },
			delay: 800,
		});

	const heading = useSpring({
		from: { opacity: 0, marginTop: 200, marginLeft: 0 },
		to: [{ opacity: 1, marginTop: 80 }, { marginLeft: 180 }],
		config: { tension: 400, friction: 40 },
	});

	const headingAdder1 = headingAdder;
	const headingAdder2 = headingAdder;
	return (
		<div>
			<Grid container direction='column' alignItems='center'>
				<animated.h1 className='heading' style={heading}>
					تایپ آنلاین
				</animated.h1>
				<animated.h1 className='heading-adder' style={headingAdder1}>
					به‌صرفه
				</animated.h1>
				<animated.h1 className='heading-adder' style={headingAdder2}>
					بی‌دغدغه
				</animated.h1>
			</Grid>
		</div>
	);
};

export default Heading;
